        if (e.getAction() != Action.RIGHT_CLICK_AIR && e.getAction() != Action.RIGHT_CLICK_BLOCK) return;

        if (e.getPlayer().getInventory().getItemInMainHand().isSimilar(PLAY_AGAIN_PAPER)) {
